import Link from "next/link";
import { useEffect, useState } from "react";
import api from "./../services/axios";
import styles from "../styles/Home.module.css";

export default function Home() {
  const [repositorios, setRepositorio] = useState([]);
  const [usuario, setUsuario] = useState(null);

  useEffect(() => {
    async function consultarUsuario() {
      try {
        const { data } = await api.get("users/m4t3us89");
        console.log("User", data);
        setUsuario(data);
      } catch (err) {
        console.log("Err", err);
      }
    }

    async function listarRepositorios() {
      try {
        const { data } = await api.get("users/m4t3us89/repos");
        console.log("Rep", data);
        setRepositorio(data);
      } catch (err) {
        console.log("Err", err);
      } finally {
        consultarUsuario();
      }
    }

    listarRepositorios();
  }, []);

  return (
    <div>
      <div className="card" style={{ width: "500px", height: "500px" }}>
        <img
          className="card-img-top"
          src={usuario?.avatar_url}
          alt="Card image cap"
        />
        <div className="card-body">
          <h5 className="card-title">Card title</h5>
          <p className="card-text">
            Some quick example text to build on the card title and make up the
            bulk of the card's content.
          </p>
          <a href="#" className="btn btn-primary">
            Go somewhere
          </a>
        </div>
      </div>

      <div className={styles.cards}>
        {repositorios.map((repositorio) => (
          <div className="card">
            <div className="card-body">
              <h5 className="card-title">{repositorio.name}</h5>
              <p className="card-text">{repositorio.description}</p>
              <a href="#" className="card-link">
                Card link
              </a>
              <a href="#" className="card-link">
                Another link
              </a>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
